import PropTypes from 'prop-types';
import React, { useState, useEffect } from 'react';

import Controls from './Controls';
import TimerDisplay from './TimerDisplay';
import IncrementDecrementButton from '../shared/IncrementDecrementButton';

const Timer = () => {
  const DefaultMinutes = 3;
  const DefaultSeconds = 0;
  let twoDigits = time => (time >= 10 ? time : `0${time}`);

  const countdownView = (minutes, seconds) => `${twoDigits(minutes) || '00'}:${twoDigits(seconds) || '00'}`;

  const [minutes, setMinutes] = useState(DefaultMinutes);
  const [seconds, setSeconds] = useState(DefaultSeconds);
  const [timerOn, setTimerOn] = useState(false);

  useEffect(() => {
    const minutes = parseFloat(localStorage.getItem('minutes'))
        || DefaultMinutes;
    const seconds = parseFloat(localStorage.getItem('seconds'))
        || DefaultSeconds;

    setMinutes(minutes);
    setSeconds(seconds);
  }, []);

  const countdown = () => {
    let intervalTime = (minutes * 60)  + seconds;
    let endTime = new Date().getTime() + intervalTime * 1000 + 500

    setTimerOn(true);

    const updateClock = () => {
      let msLeft = endTime - new Date().getTime()
      // if (msLeft <= 0) {
      //     // make a ding noise
      // }
      if (msLeft >= 0) {
        let currentTime = new Date(msLeft)
        // intervalTime = Math.floor(msLeft / 1000),

        setMinutes(currentTime.getUTCMinutes())
        setSeconds(currentTime.getUTCSeconds())

        this.tick = setTimeout(
          updateClock,
          currentTime.getUTCMilliseconds() + 500
        )
      }
    }
    updateClock()
  };

  const stepUpTime = () => {
    const currentMinutes = minutes;
    const currentSeconds = seconds;
    let newMinutes;
    let newSeconds;
    if (seconds === 59) {
      newMinutes = currentMinutes + 1;
      newSeconds = 0;
      setMinutes(newMinutes);
      setSeconds(newSeconds);
    }
    else if (minutes < 5) {
      newSeconds = currentSeconds + 1;
      setSeconds(newSeconds);
    }
  }

  const stepDownTime = () => {
    const currentMinutes = minutes;
    const currentSeconds = seconds;
    let newMinutes;
    let newSeconds;
    if (seconds === 0 && minutes > 2) {
      newMinutes = currentMinutes - 1;
      newSeconds = 59;
      setMinutes(newMinutes);
      setSeconds(newSeconds);
    }
    if (seconds > 0) {
      newSeconds = currentSeconds - 1;
      setSeconds(newSeconds);
    }
  };

  const resetTimer = () => {
    stopTimer()
    setMinutes(DefaultMinutes);
    setSeconds(DefaultSeconds);
  }

  const playPause = () => {
    if (timerOn) stopTimer()
    else countdown()
  }
  const stopTimer = () => {
    setTimerOn(false);
    clearTimeout(this.tick)
  }

  return (
    <div id="countdown-timer">
      <div className="increment timer">
        <IncrementDecrementButton 
          id="timer"
          onClick={stepUpTime}
          text="+"
        />
        <IncrementDecrementButton 
          id="timer"
          onClick={stepDownTime}
          text="-"
        />
      </div>
      <TimerDisplay time={countdownView(minutes, seconds)} />
      <Controls
        playPauseClick={playPause}
        resetClick={resetTimer}
      />
    </div>
  );
};

Timer.propTypes = {
    stepUpTime: PropTypes.func,
    stepDownTime: PropTypes.func,
    countdownView: PropTypes.string,
    playPause: PropTypes.func,
    resetTimer: PropTypes.func
};

export default Timer;
