import PropTypes from 'prop-types';
import React from 'react';

import InputButton from '../shared/InputButton';

const BrewMethodInput = ({ method, setMethod }) => (
		<div className="adjustables">
		  <h2 className="inputLabel">Brew Method</h2>
		  <form className="interactions">
		    <div className="method">
		      <InputButton
		        className={method === "aeroPress" ? 'active' : 'inactive'}
		        id="aeroPress"
		        name="method"
		        value="AeroPress"
		        onClick={setMethod}
		      />
		      <label className="method" htmlFor="aeropress">
		        AeroPress
		      </label>
		    </div>

		    <div className="method">
		      <InputButton
		        className={method === "frenchPress" ? 'active' : 'inactive'}
		        id="frenchPress"
		        name="method"
		        value="frenchPress"
		        onClick={setMethod}
		      />
		      <label className="method" htmlFor="frenchPress">
		        French Press
		      </label>
		    </div>

		    <div className="method">
		      <InputButton
		        className={method === "pourOver" ? 'active' : 'inactive'}
		        id="pourOver"
		        name="method"
		        value="pourOver"
		        onClick={setMethod}
		      />
		      <label className="method" htmlFor="pourOver">
		        Pour Over
		      </label>
		    </div>
		  </form>
		</div>
);

BrewMethodInput.propTypes = {
    method: PropTypes.string,
    setMethod: PropTypes.func
};

export default BrewMethodInput;
