import PropTypes from 'prop-types';
import React from 'react';

import IncrementDecrementQuantity from 'components/IncrementDecrementQuantity'

//wrap this in form not div
const QuantityInput = ({ waterGrams, goldenRatio, updateWater }) => (<div className="adjustables"> 
    <IncrementDecrementQuantity
      name="cups"
      title="Brewed Cups"
      value={cupsFromWater(waterGrams)}
      measure="8oz"
      changeQuantity={this.updateWater}
    />
    <IncrementDecrementQuantity
      name="coffeeGrams"
      title="Ground Coffee"
      value={coffeeGramsFromWater(waterGrams, goldenRatio)}
      measure="g"
      changeQuantity={this.updateWater}
    />
    <IncrementDecrementQuantity
      name="waterGrams"
      title="Water"
      value={Math.round(waterGrams)}
      measure="g/mL"
      changeQuantity={this.updateWater}
    />
  </div>
);

QuantityInput.propTypes = {
    waterGrams: PropTypes.number,
    goldenRatio: PropTypes.number,
    updateWater: PropTypes.func
};
