import PropTypes from 'prop-types';
import React, { useState, useEffect } from 'react';

import Controls from './Controls';
import TimerDisplay from './TimerDisplay';
import IncrementDecrementButton from '../shared/IncrementDecrementButton';

const Timer = () => {
  const DefaultSeconds = 180;
  let twoDigits = time => (time >= 10 ? time : `0${time}`);

  const countdownView = (seconds) => {
    let minutes = Math.floor(seconds/60);
    let secondsLeft = seconds % 60;

    return `${twoDigits(minutes) || '00'}:${twoDigits(secondsLeft) || '00'}`; 
  } 


  const [seconds, setSeconds] = useState(DefaultSeconds);
  const [timerOn, setTimerOn] = useState(false);

  // useEffect(() => {
  //   const seconds = parseFloat(localStorage.getItem('seconds'))
  //       || DefaultSeconds;

  //   setSeconds(seconds);
  // }, []);

  //timer play pause functionality works until reset timer button is clicked,
    //reset timer updates state but then state is update from somewhere else...
  useEffect(() => {
    let tick = null;

    if (timerOn) {
      console.log("seconds:", seconds);
      let intervalTime = seconds;
      let endTime = new Date().getTime() + intervalTime * 1000 + 500;
      console.log("endTime1:", endTime);
      const updateClock = () => {
        let msLeft = endTime - new Date().getTime()
        console.log("endTime2:", endTime);
        // if (msLeft <= 0) {
        //     // make a ding noise
        // }
        if (msLeft >= 0) {
          let currentTime = new Date(msLeft);
          setSeconds(currentTime.getUTCSeconds())

          tick = setTimeout(
            updateClock,
            currentTime.getUTCMilliseconds() + 500
          );
        }
      }
      updateClock()
    } else if (!timerOn) {
      //click is always null at this point
        //interval is never being cleared
      clearInterval(tick);
    }
    return () => clearInterval(tick);
  }, [timerOn, seconds]);

  const stepUpTime = () => {
    const currentSeconds = seconds;

    if (currentSeconds < 420) {
      let newSeconds = currentSeconds + 1;
      setSeconds(newSeconds);
    }
  }

  const stepDownTime = () => {
    const currentSeconds = seconds;

    if (seconds > 0) {
      let newSeconds = currentSeconds - 1;
      setSeconds(newSeconds);
    }
  };

  const resetTimer = () => {
    setSeconds(DefaultSeconds);
    setTimerOn(false);
  }

  const playPause = () => {
    setTimerOn(!timerOn);
  }

  return (
    <div id="countdown-timer">
      <div className="increment timer">
        <IncrementDecrementButton 
          id="timer"
          onClick={stepUpTime}
          text="+"
        />
        <IncrementDecrementButton 
          id="timer"
          onClick={stepDownTime}
          text="-"
        />
      </div>
      <TimerDisplay time={countdownView(seconds)} />
      <Controls
        playPauseClick={playPause}
        resetClick={resetTimer}
      />
    </div>
  );
};

Timer.propTypes = {
    stepUpTime: PropTypes.func,
    stepDownTime: PropTypes.func,
    countdownView: PropTypes.string,
    playPause: PropTypes.func,
    resetTimer: PropTypes.func
};

export default Timer;
